-- Cấu trúc bảng cho bảng `muasach`
--

CREATE TABLE `muasach` (
  `NguoiDungID` int(11) NOT NULL,
  `SachID` int(11) NOT NULL,
  `NgayMua` date DEFAULT current_timestamp()
