--
-- Cấu trúc bảng cho bảng `sach`
--

CREATE TABLE `sach` (
  `ID` int(11) NOT NULL,
  `TenSach` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `TrangThai` varchar(50) DEFAULT NULL,
  `TacGia` varchar(50) DEFAULT NULL,
  `NgayHoanThanh` date DEFAULT NULL,
  `NgaySangTac` date DEFAULT current_timestamp(),
  `SoChuong` int(11) DEFAULT NULL,
  `URLBiaSach` varchar(255) DEFAULT NULL,
  `GiaSach` int(11) DEFAULT NULL,
  `TheLoai` varchar(50) DEFAULT NULL,
  `MoTa` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `sach`
--

INSERT INTO `sach` (`ID`, `TenSach`, `TrangThai`, `TacGia`, `NgayHoanThanh`, `NgaySangTac`, `SoChuong`, `URLBiaSach`, `GiaSach`, `TheLoai`, `MoTa`) VALUES
(1, 'Gửi thanh xuân', 'Đang ra', 'Mộc Lan', NULL, '2023-02-15', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/289.jpg?v=1&time=1675083462&w=720&h=720', 50, 'Tiểu thuyết', 'Mộc Miên là một người hướng nội chính hiệu, dù ít khi ra ngoài nhưng thế giới trong mắt Mộc Miên không hề nhỏ bé, trong thời đại công nghệ phát triển chỉ cần ngồi một chỗ vẫn có thể biết được tin tức bên ngoài.\r\nTrong căn phòng ấm áp yên tĩnh, khoé môi cô gái nhỏ khẽ cong lên theo từng con chữ, Mộc Miên dùng trí tưởng tượng phong phú của mình để đắm mình vào câu chuyện của các nhân vật bên trong cuốn sách.\r\nMay là cái kết đã không khiến cô thất vọng, cặp đôi chính cô yêu mến cuối cùng đã đến được với nhau.\r\nTuy nhiên Mộc Miên lật giở đến vài chương đầu sách, vì sao tác giả lại lấy cái tên Mộc Miên để đặt cho nữ phụ đáng thương chứ? Cô gái cảm thấy không hài lòng với nhân vật mang tên mình nhưng lại có kết cục bi thảm. Hình như ông trời cũng nghe được nỗi lòng của cô, quyết định cho Mộc Miên cơ hội “cứu vớt” nữ phụ.\r\nThục nữ hướng nội xuyên vào nữ phụ đoản mệnh, kề cận bên cặp đôi mình yêu thích, vừa tiến hành công cuộc giữ mạng, liệu Mộc Miên có thành công không? Mời các bạn cùng theo dõi tác phẩm nhé!'),
(2, 'Tiệm bánh hạnh phúc', 'Hoàn thành', 'RinBaBa', NULL, '2023-02-15', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/660.jpg?v=1&time=1675823339&w=720&h=720', 60, 'Truyện ngắn', 'Hôm ấy, trước trung tâm thương mại đông đúc có chàng trai đứng đợi một người.\r\nGiữa trời đông vào giờ tan tầm, trên đường phố tấp nập người qua lại có không ít người tò mò ngoái lại nhìn chàng trai trẻ rồi bất giác mỉm cười vì hình ảnh ấm áp và đẹp đẽ. Các cô gái trẻ đi cùng nhau thì trầm trồ không dứt. Các cô ước gì có một người bạn trai như thế...'),
(3, 'Trở về cùng những cơn mưa', 'Hoàn thành', 'RinBaBa', NULL, '2023-02-15', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/657.jpg?v=1&time=1675823079&w=720&h=720', 40, 'Truyện ngắn', 'Mưa rả rích suốt nhiều giờ. Mặt đất đã ẩm ương đến tội nghiệp. Những hàng cây nghiêng oằn xuống đất. Dưới gốc cây, những cánh hoa vụn vỡ như bị bàn tay vô hình vò nát. Chỉ duy nhất mùi hương nhè nhẹ lại thanh thuần len lỏi trong tiếng mưa...'),
(4, 'Về nhà thôi', 'Hoàn thành', 'Hân Như', NULL, '2023-02-15', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/651.jpg?v=1&time=1675669416&w=720&h=720', 40, 'Tiểu thuyết', 'Internet là một nơi ẩn chứa đầy sự tối tăm và sa đọa, còn chúng tôi là công dân thời đại công nghệ số, nghèo kỹ năng sống, giàu sự cô đơn, thích giam mình trong thế giới ảo và thừa thãi lòng hoài nghi.'),
(5, 'Nước vẫn mênh mông dưới chân đồi', 'Hoàn thành', 'Phạm Tử Văn', NULL, '2023-02-15', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/642.jpg?v=1&time=1675407053&w=720&h=720', 45, 'Truyện ngắn', 'Ngoài trời, mưa mỗi lúc một nặng hạt. Bình ngồi thu lu trên giường dõi mắt qua khe cửa nhìn ra ngoài. Mẹ Bình ngồi ở phía sau, ngao ngán dựa lưng vào tường và nén thả vào không gian tiếng thở dài não nuột. Bình quay lại nhìn mẹ nhưng không nói gì...'),
(6, 'Chiến lược chăn voi', 'Hoàn thành', 'Steve Kplan', '2023-03-07', '2023-03-07', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/42023.jpg?v=1&w=340&h=497', 20, 'Tuổi trẻ', 'Tôi cho rằng Chiến lược “chăn Voi” - Chinh phục và giữ chân khách hàng lớn là một kiến giải hết sức thực tiễn và dễ tiếp cận nhằm giúp những nhà kinh doanh có thể chăm sóc các đối tác chiến lược của mình.\r\n\r\nCuốn sách này là hành trang cho bạn – cũng như những đồng đội của bạn – trên con đường chinh phục thành công dài hạn.\r\n\r\nCuốn sách sẽ tiết lộ cách để thấu hiểu văn hóa và cơ cấu tổ chức, cũng như những điều cần biết khác về các đối tác lớn; bên cạnh đó, nó đưa ra những ý tưởng và phương pháp để kiện toàn danh sách khách hàng tiềm năng, nâng cao hiệu quả diễn thuyết, xây dựng đội ngũ môi giới “tay trong”1 tại các công ty khách hàng của bạn, cũng như thương thảo thành công hợp đồng.\r\n\r\nCuốn sách đưa ra những phương thức tiếp cận hướng giá trị nhằm xây dựng các liên minh, đáp ứng nhu cầu khách hàng cũng như đương đầu với khủng hoảng. Và hơn hết, Chiến lược “chăn Voi” giúp bạn tránh được những sai lầm có thể khiến đoàn tàu kinh doanh của bạn trật bánh.'),
(7, 'Đừng bao giờ chia đôi lợi ích trong mọi cuộc đàm phán', 'Đang ra', 'Chris Voss, Tahl Raz', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/43358.jpg?v=1&w=340&h=497', 20, 'Phát triển bản thân', 'Đừng bao giờ chia đôi lợi ích trong mọi cuộc đàm phán – có thể bạn sẽ ngạc nhiên khi biết rằng đây chính là lời khuyên từ một chuyên gia đàm phán giải cứu con tin dày dạn kinh nghiệm của FBI, khi mà quan điểm của đàm phán hiện nay là đặt nguyên tắc win-win (đôi bên cùng có lợi) lên hàng đầu.\r\n\r\nTuy nhiên,\r\n\r\nCuốn sách này có thể giúp bạn thương lượng thành công mức lương bạn muốn với sếp.\r\nCuốn sách này có thể giúp bạn mặc cả khi mua hàng, thỏa thuận giảm tiền thuê nhà trong khi chủ nhà vừa thông báo tăng giá thuê.\r\nCuốn sách này có thể giúp bạn thuyết phục được những khách hàng khó tính nhất ký hợp đồng với công ty bạn.\r\nCuốn sách này thậm chí có thể giúp bạn đàm phán giải cứu con tin thành công!\r\nTại sao ư?\r\n\r\nBởi cuốn sách này tập hợp những kỹ năng đúc kết từ kinh nghiệm của một chuyên gia đàm phán giải cứu con tin có thâm niên làm việc hơn 20 năm tại Cục Điều tra Liên bang Mỹ FBI. Ông coi đàm phán là trung tâm của sự hợp tác, là một cuộc giao tiếp hiệu quả, không phải một trận đấu khẩu cạnh tranh gay gắt, cũng không phải một buổi nhượng bộ thỏa hiệp bất công.\r\n\r\nVới quan niệm “Cuộc sống là những cuộc đàm phán”, tác giả Chris Voss đã cung cấp 9 cách thức để chiến thắng mọi cuộc đàm phán, từ đó giúp ta thay đổi được cuộc đời của mình và thành công trong cuộc sống – như chính tác giả và các học viên của ông đã trải qua.\r\n\r\nNhững kỹ năng đó là gì?\r\n\r\nĐó là kỹ năng “Lắng nghe chủ động”, lặp lại những gì đối phương đã nói và im lặng để khoảng lặng phát huy tác dụng.\r\nĐó là kỹ năng nói “Không”: từ “Không” là một cách thức uy lực để bắt đầu mọi cuộc đàm phán, chứ không phải từ “Có”, “Vâng” hay “Bạn đúng” – những từ mà mọi người luôn cố gắng đạt được nhưng thực ra lại giết chết thỏa thuận.\r\nĐó là kỹ năng Tìm kiếm Thiên Nga Đen – vũ khí quý hiếm nhất trong một cuộc đàm phán giúp bạn đạt được thành công vang dội.\r\n…\r\n\r\nKhông màu mè, phô trương, cuốn sách này cực kỳ dễ dàng áp dụng và hữu ích với tất cả mọi người, bởi các kỹ năng tác giả cung cấp cho chúng ta không phải là mớ lý thuyết về tâm lý học khô khan và khó hiểu, mà chúng đều được hình thành từ thực tiễn và phục vụ cuộc sống.'),
(8, 'Đừng trở nên xấu xa', 'Đang ra', 'Rana Foroohar', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/43082.jpg?v=1&w=340&h=497', 30, 'Phát triển bản thân', 'Có những quyển sách ra đời từ những ý tưởng trừu tượng, to tát; một số quyển khác bắt đầu từ những điều gần gũi hơn. Quyển gần đây nhất của tôi, Makers and Takers (tạm dịch: Người lao động và kẻ cưỡng đoạt), xuất phát từ những cuộc đối thoại chính sách cấp cao về ngành tài chính. Còn quyển sách bạn đang cầm trên tay nói về các tổn hại kinh tế, chính trị và nhận thức do ngành công nghệ gây ra trong hai mươi năm qua dưới một lăng kính rộng hơn. Nhưng sự ra đời của nó cũng khá gần gũi.\r\n\r\nMọi chuyện bắt đầu vào một buổi chiều cuối tháng Tư năm 2017, khi tôi đi làm về, mở sao kê thẻ tín dụng và bị sốc: có hơn 900 đô-la các khoản phí trên trời rơi xuống từ cửa hàng ứng dụng của Apple (App Store). Ban đầu, tôi nghĩ tài khoản của mình đã bị trộm mật khẩu. Nhưng sau khi tìm hiểu kỹ hơn, tôi phát hiện ra cậu con trai mười tuổi của mình chính là “tác giả” của những khoản chi này: thằng bé đã mua “cầu thủ” cho trò chơi bóng đá trực tuyến mà nó yêu thích.'),
(9, 'Làm giàu theo cách bá đạo', 'Hoàn thành', 'Jen Sincero', '2023-03-07', '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/42197.jpg?v=1&w=340&h=497', 50, 'Phát triển bản thân', 'Chúng ta được nuôi dạy để tin rằng phải làm việc chăm chỉ để kiếm tiền, và chắc chắn rằng điều này là đúng, nhưng bí mật thực sự là bạn phải chấp nhận những sự mạo hiểm khổng lồ. Bạn phải làm những việc chưa từng làm trước đây để khiến bản thân được chú ý, để nhận ra sự tuyệt vời của chính mình, để mạo hiểm bị coi là kẻ ngốc.\r\n\r\nBạn không những phải thừa nhận khao khát và quyết tâm làm giàu, mà quan trọng nhất là bạn phải cho phép bản thân làm vậy. Chấp nhận sự không thoải mái, nhưng nó là kiểu không thoải mái mà một nửa là Eo ơi! một nửa là Tuyệt quá! Sự sợ hãi và niềm phấn khích là hai mặt của một đồng xu, và nó chính là thứ không thoải mái mà tôi đang nói đến. Nó còn có tên gọi khác là “cơn hưng phấn”, nó là sự không thoải mái cần thiết, sự gay cấn của việc sống hết mình và tự chủ.\r\n\r\nTôi hy vọng bạn sẽ đọc đi đọc lại cuốn sách này và làm theo những gì được viết, rằng bạn sẽ lắng nghe tiếng gào thét trong tâm can thay vì những nghi ngờ và sợ hãi, và rằng bạn sẽ tiếp tục dũng cảm dấn bước vào tương lai vô định. Tôi đã thấy không biết bao nhiêu khách hàng, bạn bè và những người tôi gặp tại các bữa tiệc đang khổ sở và vật lộn vì tiền, nó giống như việc nhìn thấy người chết vì đói trong khi có hẳn một bữa buffet ăn-thoải-mái ngay dưới sảnh. Số tiền mà bạn ao ước đang ở ngay trước mặt bạn. Những cơ hội, khách hàng, người cố vấn, ý tưởng kiếm tiền tuyệt vời – tất cả chúng đều ở ngay đây, ngay lúc này, chờ bạn thức tỉnh, nhận ra chúng và bắt đầu bữa tiệc của đời mình.'),
(10, 'Ping – Giải cứu vườn địa đàng', 'Hoàn thành', 'Stuart Avery Gold', '2023-03-08', '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/43145.jpg?v=1&w=340&h=497', 50, 'Phát triển bản thân', 'Nối tiếp hành trình “Vượt ao tù ra biển lớn”, Ping dấn bước vào cuộc phiêu lưu thứ ba đi tìm nguyên nhân và giải pháp cho những thay đổi bất thường của tự nhiên: Giải cứu Vườn địa đàng.\r\n\r\nTrong phiên bản mới nhất này, “Ping – Giải cứu Vườn địa đàng” gồm cả ba phần: Vượt khỏi ao tù; Hành trình ra biển lớn và phần mới nhất: Giải cứu Vườn địa đàng, mang đến cho người đọc một câu chuyện hoàn chỉnh về hành trình đầy thú vị của chú ếch Ping.\r\n\r\nSau chuyến hành trình đầu tiên, vượt khỏi ao tù thành công, đến hành trình thứ hai cuộc hành hương ra biển lớn với hai chú ếch trẻ là Daikon và Hodo, và bây giờ Ping trở lại cuộc phiêu lưu thứ 3 đi tìm nguyên nhân và giải pháp cho những thay đổi bất thường của tự nhiên. Ping gặp rắc rối bởi những dấu hiệu đáng lo ngại về sự mất cân bằng ngày càng tăng trong Eden Pond, buộc chú phải dấn thân vào chuyến phiêu lưu vĩ đại nhất từ trước đến nay của mình: hành trình dẫn dắt thế giới đang lầm đường lạc lối quay về đúng con đường hòa hợp của mình.\r\n\r\nThông qua những chuyến đi đến những vùng đất mới, gặp gỡ những sinh vật mới và cả những chuyến đi vào chính tâm tưởng của mình, Ping đã rút ra được cho bản thân cũng như chia sẻ cho muôn loài những bài học quý giá, những chân lý tưởng chừng như giản đơn nhưng có giá trị muôn đời: Ai cũng có thể tạo dựng một cuộc sống tốt đẹp thông qua những lựa chọn và hành động của chính bản thân.'),
(11, 'Cẩm nang phòng chống tin giả', 'Hoàn thành', 'Nhiều tác giả', '2023-03-08', '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/42260.jpg?v=1&w=340&h=497', 60, 'Phát triển bản thân', 'Chiều 27/12, Cẩm nang phòng chống tin giả, tin sai sự thật trên không gian mạng đã được công bố tới đông đảo người dùng.\r\nTin giả, tin sai sự thật đã gây ra nhiều hệ lụy, thiệt hại kinh tế, ảnh hưởng danh dự, uy tín, trật tự an toàn xã hội… Nhằm nâng cao nhận thức và tăng sức đề kháng của người dân trước tin giả tràn lan trên mạng, Bộ Thông tin và Truyền thông trong suốt 8 tháng qua đã xây dựng cuốn cẩm nang về phòng chống tin giả. Và chiều 27/12 tại Hà Nội, cuốn cẩm nang đã được công bố tới đông đảo người dùng.\r\n\r\nCẩm nang được xây dựng với 2 phiên bản: phiên bản sách in và phiên bản điện tử. Cẩm nang cho phép người dùng có thể nhanh chóng nắm được thông tin như: dấu hiệu nhận biết một tin giả; cách xác định tin giả; làm thế nào để tránh bẫy tin giả... Thông qua cuốn cẩm nang, người dùng cũng sẽ biết được phải làm gì nếu lỡ đăng tải, chia sẻ tin giả tin sai sự thật, tin sai sự thật; cách xử lý khi thấy tin giả...\r\n\r\nCẩm năng đặc biệt nhấn mạnh tới hành động có trách nhiệm trên không gian mạng và cũng đưa ra các mức phạt hành chính và xử lý hình sự của việc cung cấp chia sẻ tin giả tin sai sự thật. Mức phạt hành chính cao nhất là 70.000.000 đồng và phạt tù lên tới 12 năm.\r\n\r\nCẩm nang có ý nghĩa quan trọng, cung cấp các thông tin, kỹ năng cơ bản nhất để các cơ quan, tổ chức, cá nhân tham gia không gian mạng xử lý tin giả hiệu quả nhất.\r\n\r\nPhiên bản điện tử sẽ tiếp tục là một cuốn cẩm nang mở để cập nhật các hình thức tin giả, lừa đảo xuất hiện mới trên không gian mạng, từ đó nhanh chóng đưa ra khuyến cáo tới người dùng.'),
(12, 'Happy Book - Hạnh phúc trong từng hơi thở', 'Hoàn thành', 'Nguyễn Mạnh Hùng', '2023-03-08', '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/42020.jpg?v=1&w=340&h=497', 40, 'Tuổi trẻ', 'Thời gian thấm thoát thoi đưa. 365 ngày của một năm trôi đi quá nhanh. Ấy vậy mà đã đến Tết và chúng ta cùng nhau đón năm mới Đinh Dậu 2017. Con gà của năm mới này nhắc chúng ta chăm chỉ hơn và dậy sớm hơn, thư giãn và bình an hơn, yêu thương và san sẻ nhiều hơn, tốt bụng và phụng sự nhiều hơn.\r\n\r\nTôi nhớ rằng mình đã cảm thấy rất tuyệt vời khi cuốn sách Happy Book – Hạnh phúc mỗi ngày được xuất bản trước thềm năm mới Bính Thân 2016 và được bạn đọc đón nhận tích cực. Bốn ngàn cuốn sách in ra đã được bạn đọc đón nhận và rất nhiều lời cám ơn, nhiều cảm xúc chân thành, nhiều sự khích lệ đã được gửi đến tôi. Hạnh phúc đến với tôi mỗi ngày và luôn trào dâng không ngớt. Nhận được lợi lạc của cuốn sách với mọi người, tôi quyết định cho ra mắt Happy Book – hạnh phúc mỗi phút giây. Cuốn sách là tập hợp 112 câu nói ấn tượng, có sức nuôi dưỡng và có tính nhắc nhở để chúng ta luôn tìm được hạnh phúc và bình an. Tôi mong muốn cuốn sách sẽ là món quà để mọi người tặng nhau nhân dịp năm mới, để lì xì nhân ngày Tết đến Xuân về. Bởi từ gần chục năm nay Thái Hà Books đã tổ chức Tết Sách và phát động phong trào lì xì sách.'),
(13, 'Dám hạnh phúc', 'Đang ra', 'Kishimi Ichiro, Koga Fumitake', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/41177.jpg?v=1&w=340&h=497', 30, 'Tuổi trẻ', 'Sau thành công của “Dám Bị Ghét”, “Dám Hạnh Phúc” tiếp tục được hai triết gia nổi tiếng Nhật Bản là Kishimi Ichiro và Koga Fumitake gửi gắm tới mọi người như sự hiện thân của tình yêu chân chính. Một lần nữa, lối văn tranh luận giữa chàng thanh niên và nhà triết gia được tác giả áp dụng và tiếp nối câu chuyện “Dám Bị Ghét”. Tại tác phẩm lần này, sau khi đã thấm nhuần tư tưởng Alder, anh ta tiếp tục quay trở lại than phiền với nhà triết gia, và tình yêu chính là con đường mà nhà triết gia hướng đến cho chàng thanh niên trong lần trở lại này. Câu chuyện tưởng chừng như không liên quan, nhưng nếu nghiền ngẫm cả hai tác phẩm “Dám Bị Ghét” và “Dám Hạnh Phúc” sẽ tìm ra sợi dây kết nối hoàn hảo. Năm phần chính, tương đương với năm quan điểm về cuộc sống là sự dẫn dắt dòng tư duy, giúp thính giả có cảm giác nhập tâm vào chính cuộc đối thoại, chính điều đó đã góp phần tạo sự lôi cuốn trong tác phẩm. Với “Dám Hạnh Phúc”, thính giả sẽ thấm nhuần sâu hơn về lòng can đảm, cách để có được hạnh phúc, về mối quan hệ tình yêu – tự lập,…\r\n\r\nTrân trọng giới thiệu!'),
(14, ' Dám bị ghét', 'Hoàn thành', 'Kishimi Ichiro, Koga Fumitake', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/1/41174.jpg?v=1&w=340&h=497', 30, 'Tuổi trẻ', '\"Dám Bị Ghét\" là cuốn sách tâm lý kinh điển thuộc top best seller tại Nhật Bản. Tác phẩm được chắp bút bởi hai tác giả nổi tiếng và có sức ảnh hưởng trong lĩnh vực tâm lý và văn học của xứ Mặt Trời Mọc là Kishimi Ichiro và Koga Fumitake. Dưới hình thức của một cuộc trò chuyện, tác phẩm phân tích những cách thức cân bằng mối quan hệ trong xã hội và cuộc sống, khơi gợi những góc khuất tâm lý con người. Bởi vậy, tác phẩm phù hợp với mọi đối tượng đang cảm thấy vô định trên con đường đời. Năm chương trong tác phẩm được chia tương ứng với những cuộc trò chuyện khác nhau của chàng thanh niên trẻ tuổi đang tìm hiểu cuộc đời và triết gia giàu kinh nghiệm – người sẽ lý giải những thắc mắc của chàng trai về cuộc sống, mối quan hệ giữa người với người dựa trên nền tảng tâm lý học Adler,…. Được đánh giá là một tác phẩm mang tính chất “self-help”, cuốn sách mang nhiều tầng ý nghĩa, thông điệp và quan niệm sống, triết lý nhân sinh để mọi người có cái nhìn cởi mở, mới mẻ hơn trong cuộc sống.\r\n\r\nTrân trọng giới thiệu!'),
(15, 'Thế giới em nhìn thấy', 'Hoàn thành', 'RinBaBa', '2023-03-08', '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/658.jpg?v=1&time=1675823170&w=720&h=720', 50, 'Truyện ngắn', 'Giữa ánh đèn rực rỡ, em tỏa sáng như một nữ thần. Em dâng lời ca tiếng hát đến cho mọi người, nhưng thứ họ quan tâm lại là hình thể, là quần áo và những chỗ hở hang trên người em. Họ gào thét nghệ danh của em nhưng mấy ai biết tên thật của em là gì. Thứ họ quan tâm là lớp mặt nạ bên ngoài được đắp nặn bằng truyền thông, bằng tiền, bằng những bài viết sáo rỗng...'),
(16, 'Duyên tình chưa trả cho ai', 'Hoàn thành', 'Cuồng Ngôn Loạn Ngữ', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/302.jpg?v=1&time=1675159317&w=720&h=720', 30, 'Truyện ngắn', 'Trích đoạn:\r\n\" Không ngờ, người ta tin tưởng nhất lại rời bỏ ta...\r\nChàng đi, mang theo ánh sáng của ta\r\nTa không hối hận, vì ta yêu chàng\r\nNhưng...\r\nSao chàng nhẫn tâm\r\nĐể mình ta cô độc, ôm trái tim rỉ máu trong bóng đêm cô tịch vĩnh hằng... \"\r\n'),
(17, 'Lá úa, biển xanh!', 'Đang ra', 'Freesia Phan', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/292.jpg?v=1&time=1675083424&w=720&h=720', 40, 'Truyện ngắn', 'Lá úa, biển xanh!'),
(18, 'Những tháng năm vội vã', 'Đang ra', 'Minh Nguyệt Vô Ưu', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/248.jpg?v=1&time=1678241652&w=720&h=720', 30, 'Tiểu thuyết', 'Việt Nam vào giai đoạn nửa thuộc địa nửa phong kiến, thời kỳ nhiễu nhương, dở dở ương ương, con người vẫy vùng giữa cái mới và cái cũ. Trong tư tưởng giằng co đó thì những mối tình đồng giới bị xem là thứ bệnh truyền nhiễm cần được chữa trị. Hai người đàn ông sinh nhầm thời. Vì định kiến xã hội mà họ phải bỏ lỡ nhau đến bảy năm. Năm tháng vội vã, thời gian như thoi đưa. Trong bảy năm đó, họ có tất cả nhưng lại không thể có nhau…\r\n“Minh khẽ lắc đầu. Thèm chè gì chứ? Chẳng qua hắn đang thèm một số chuyện của quá khứ mà thôi. Nơi đây từng là chỗ hẹn hò của hắn. Từng có người cùng hắn đêm mười sáu trăng tròn ngồi trên xuồng ăn chè, kể nhau nghe chuyện phong hoa tuyết nguyệt, nói về những dự tính của tương lai. Đáng tiếc, tất cả đều không thành. Bởi vì trong cái tương lai đó, hai người đã không còn bên nhau nữa.”'),
(19, 'Tiếng sáo nơi lầu cao', 'Đang ra', 'Miêu Nguyệt', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/268.jpg?v=1&time=1675179596&w=720&h=720', 50, 'Tiểu thuyết', 'Hắn chỉ là một người hầu thấp kém lại nguyện một đời bầu bạn cùng người vị thiếu gia của mình. Nhưng khi người đến cầu thân xuất hiện, hắn phát hiện khao khát của mình đối với thiếu gia không chỉ dừng ở đó. Còn vị thiếu gia vì để muốn thoát khỏi sự ràng buộc với người cha điên cuồng kia mà sẵn sàng chấp nhận mối hôn sự không cần tình yêu.\r\nMột người hầu nhỏ bé như hắn liệu đưa tay ra thiếu gia có nắm lấy tay hắn không?'),
(20, 'Vòng xoáy nước mắt', 'Đang ra', 'Ngọc Hà', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/272.jpg?v=1&time=1673150509&w=720&h=720', 30, 'Tiểu thuyết', 'Vòng xoáy nước mắt!'),
(21, 'Chầm chậm yêu anh cả một đời', 'Đang ra', 'CacaoNong', NULL, '2023-03-08', NULL, 'https://307a0e78.vws.vegacdn.vn/view/v2/image/img.cdv_novel/0/0/0/209.jpg?v=1&time=1672720724&w=720&h=720', 40, 'Tiểu thuyết', 'Lâm Tiên là một cô gái điềm đạm và có bản lĩnh. Nhưng vì một số lý do gia đình, cô quyết định kết hôn giả với đối tượng vừa xem mắt lần đầu_Trịnh Hào, một kĩ sư giỏi và khá điển trai, nhưng gặp phải một tai nạn khiến cho hai chân đi khập khiễng.\r\n\r\nHai mảnh ghép tưởng chừng như chẳng hề liên quan với nhau, được vũ trụ đưa đẩy vào bức tranh của một cuộc hôn nhân trên danh nghĩa.\r\n\r\nNgày ấy gặp nhau độ lá già,\r\n\r\nTrải bao đông lạnh, đón xuân qua.\r\n\r\nChẳng mong nhàn hạ vui suốt kiếp,\r\n\r\nChỉ ước cùng anh mỗi xế tà.');

